def start_exec_engine():
 print("[FileExecEngine] ðŸš€ Watching ptm_inbox...")
 os.makedirs("ptm_inbox", exist_ok=True)
 os.makedirs("executed", exist_ok=True)
 while True:
  for f in os.listdir("ptm_inbox"):
   if f.endswith(".py"):
    p = os.path.join("ptm_inbox", f)
    subprocess.run(["python3", p], check=True)
    os.rename(p, os.path.join("executed", f))
  time.sleep(5)
